import {
    EllipseFigureType,
    Figures,
    PointType,
    RectFigureType,
    NamesForUpdate,
    TextFigureType,
    TypesFigureType
} from "./mainType";
import uuid from "react-uuid";
import {COLORS} from "./constantsColor";
import {PlaceType} from "./reducers/sectorsReducer";
import {SIZE_CIRCLE, SIZE_IDENT_CIRCLE} from "../components/figures/sectors/cacheCircle";

export const createRect = (move: PointType, mouseDown: PointType): RectFigureType => {
    return {
        id: uuid(),
        typeFigure: Figures.RECT,
        isSelected: false,
        x: mouseDown.x,
        y: mouseDown.y,
        w: move.x - mouseDown.x,
        h: move.y - mouseDown.y,
        bgColor: COLORS.bgFigure,
        borderWidth: 0,
        borderColor: COLORS.transparent,
        rotation: 0,
        cornerRadius: 0,
    }
}

export const createEllipse = (move: PointType, mouseDown: PointType): EllipseFigureType => {
    return {
        id: uuid(),
        typeFigure: Figures.ELLIPSE,
        isSelected: false,
        x: mouseDown.x,
        y: mouseDown.y,
        radiusX: move.x - mouseDown.x,
        radiusY: move.y - mouseDown.y,
    }
}

export const createText = (mouseDown: PointType): TextFigureType => {
    return {
        id: uuid(),
        typeFigure: Figures.TEXT,
        isSelected: false,
        x: mouseDown.x,
        y: mouseDown.y,
        text: 'Hello'
    }
}


type checkTypeActionAction = {
    isSelection: boolean, isDrawGrid: boolean,
    figure: TypesFigureType | null
}
export const checkTypeActionForCursor = ({isSelection, isDrawGrid, figure}: checkTypeActionAction) => {

    const container = document.getElementById('section_container');

    if (container) {
        typeCursorSelectArea(container, isSelection);
        typeCursorDraw(container, isDrawGrid, figure !== null);
    }
}

const typeCursorSelectArea = (container: HTMLElement, isSelection: boolean) => {

    isSelection
        ? container.classList.add('cursor-selection')
        : container.classList.remove('cursor-selection');
}

const typeCursorDraw = (container: HTMLElement, isDrawGrid: boolean, isFigure: boolean) => {
    if (isDrawGrid || isFigure) {
        container.classList.add('cursor-draw-grid')
    } else {
        container.classList.remove('cursor-draw-grid');
    }
}

export const checkNameFigureForNumber = (name: string | undefined) => {

    const valueNames = Object.values(NamesForUpdate);

    return valueNames.some(el => el === name);
}

export const checkSizeInterval = (places: PlaceType[]) => {

    let placeOne: PlaceType | null = null;
    let placeVert: PlaceType | null = null;
    let placeHor: PlaceType | null = null;

    places.forEach(place => {
        if (place.numCol === 1 && place.numRow === 1) {
            placeOne = place
        }
        if (place.numCol === 1 && place.numRow === 2) {
            placeVert = place;
        }
        if (place.numCol === 2 && place.numRow === 1) {
            placeHor = place;
        }
    })
    if (placeOne && placeVert && placeHor) {
        //@ts-ignore todo
        return {horizontal: placeHor.x - placeOne.x - SIZE_CIRCLE, vertical: placeVert.y - placeOne.y - SIZE_CIRCLE}
    }
    return {horizontal: SIZE_IDENT_CIRCLE, vertical: SIZE_IDENT_CIRCLE}
}

export const createArraysPointRegion = (start: PointType, end: PointType) => {

    const pointsX: number[] = [];
    const pointsY: number[] = [];

    if (start.x < end.x) {

        for (let x = start.x; x <= end.x; x++) {
            pointsX.push(x);
        }
    } else {

        for (let x = end.x; x <= start.x; x++) {
            pointsX.push(x);
        }
    }

    if (start.y < end.y) {

        for (let y = start.y; y <= end.y; y++) {
            pointsY.push(y);
        }
    } else {

        for (let y = end.y; y <= start.y; y++) {
            pointsY.push(y);
        }
    }

    return {pointsX, pointsY}
}

export const checkMaxColumnOrRowInSector = (sector: PlaceType[],type: 'numRow' | 'numCol'): number => {

    let maxValue = 0;

    sector.forEach(place => {

        if (place[type] > maxValue) {
            maxValue = place.numCol;
        }
    })

    return maxValue;
}

export const cacheMiddleColumnPlace = (sector: PlaceType[], numCol: number) => {

    const columns: {[key:string]: PlaceType} = {}

    sector.forEach(place => {

        if (place.numCol === numCol) {

            columns[place.numRow] = place;
        }
    })

    return columns;
}

export const searchCenterCircle = (sectorPlaces: PlaceType[], maxCol: number): { [key: string]: PointType } => {

    let centerCircle: { [key: string]: PointType } = {};
    let startPlaceRows: { [key: string]: PlaceType } = {};

    sectorPlaces.forEach(place => {

        if (place.numCol === 1) {
            startPlaceRows[place.numRow] = place;
        }
        if (place.numCol === maxCol) {
            centerCircle[place.numRow] = {x: (place.x + startPlaceRows[place.numRow].x) / 2, y: place.y}
        }
    })

    return centerCircle;
}


type SideTriangleType = {hypotenuse: number, katetA: number ,katetB: number}
export const calcSidesTriangle = (centerCircle:PointType, place: PlaceType, corner: number): SideTriangleType => {

    const RADIAN = 0.0174533;

    const hypotenuse = Math.abs(centerCircle.x - place.x);//radius
    const katetB = hypotenuse * Math.abs(Math.sin(corner * RADIAN));
    const katetA = Math.sqrt(Math.pow(hypotenuse,2) - Math.pow(katetB,2));

    return {hypotenuse, katetA: +katetA.toFixed(2) ,katetB: +katetB.toFixed(2)}
}

export const checkCurveForPlace = (place: PlaceType, curve: number, middle: number[], triangle: SideTriangleType, centerPlaces:{ [key: string]: PointType }): PlaceType => {

    if (curve >= 0) {

        if (place.numCol < middle[0]) {

            place.x = centerPlaces[place.numRow].x - triangle.katetA;
            place.y = centerPlaces[place.numRow].y - triangle.katetB;

        } else if (middle[1] && place.numCol > middle[1]) {

            place.x = centerPlaces[place.numRow].x + triangle.katetA;
            place.y = centerPlaces[place.numRow].y - triangle.katetB;
        }else if (!middle[1] && place.numCol > middle[0]) {

            place.x = centerPlaces[place.numRow].x + triangle.katetA;
            place.y = centerPlaces[place.numRow].y - triangle.katetB;
        }
    }else {

        if (place.numCol < middle[0]) {

            place.x = centerPlaces[place.numRow].x - triangle.katetA;
            place.y = centerPlaces[place.numRow].y + triangle.katetB;

        } else if (middle[1] && place.numCol > middle[1]) {

            place.x = centerPlaces[place.numRow].x + triangle.katetA;
            place.y = centerPlaces[place.numRow].y + triangle.katetB;
        }else if (!middle[1] && place.numCol > middle[0]) {

            place.x = centerPlaces[place.numRow].x + triangle.katetA;
            place.y = centerPlaces[place.numRow].y + triangle.katetB;
        }
    }

    return place;
}

export const resetCachePlace = (places: PlaceType[], cachePlace: PlaceType[], centerPlaces:{ [key: string]: PointType }): PlaceType[] => {

    const maxCol = checkMaxColumnOrRowInSector(places, 'numCol');
    let middle = [Math.ceil(maxCol / 2)];

    if (maxCol % 2 === 0) {
        middle.push(middle[0] + 1)
    }

    let result: PlaceType[] = [];

    for( let i = 0; i < cachePlace.length; i++) {

        const placeCache = cachePlace[i];
        const place = places[i];

        const triangle = calcSidesTriangle(centerPlaces[place.numRow], placeCache, 0);

        result[i] = checkCurveForPlace(place,0, middle, triangle, centerPlaces);
    }

    return result;
}

import {PlaceType} from "./reducers/sectorsReducer";

export type PointType = {
    x: number,
    y: number,
}

export enum Figures {
    "RECT" = 1,
    "ELLIPSE",
    "TEXT",
    "SECTOR"
}

export const NamesForUpdate = {
    x: 'x',
    y: 'x',
    w: 'w',
    h: 'h',
    rotation: 'rotation',
    cornerRadius: 'cornerRadius',
}

export type TypesFigureType = typeof Figures.RECT | typeof Figures.ELLIPSE | typeof Figures.TEXT | Figures.SECTOR
export type MainParamFigureType = {
    id: string,
    typeFigure: TypesFigureType,
    isSelected: boolean
}

export type RectFigureType = PointType & MainParamFigureType & {
    w: number,
    h: number,
    bgColor: string
    borderWidth: number,
    borderColor: string,
    rotation: number,
    cornerRadius: number
}

export type EllipseFigureType = PointType & MainParamFigureType & {
    radiusX: number,
    radiusY: number,
}

export type TextFigureType = PointType & MainParamFigureType & {
    text: string,
}

export type GeneralFigureType = RectFigureType | EllipseFigureType | TextFigureType | PlaceType[]

import {COLORS} from "../../../store/constantsColor";
import Konva from "konva";
import Stage = Konva.Stage;
import {PointType} from "../../../store/mainType";
import KonvaEventObject = Konva.KonvaEventObject;
import KonvaCircle= Konva.Circle;
import KonvaText= Konva.Text;

export type PlaceElementType = {
    positionPlace: PointType,
    isSelected: boolean,
    numCol: number,
    offset: PointType
    onDragStart: (e: KonvaEventObject<DragEvent>) => void,
    onDragEnd: (e: KonvaEventObject<DragEvent>) => void
}

export const SIZE_CIRCLE = 20;
export const SIZE_IDENT_CIRCLE = 5;

export let circleCache: KonvaCircle;
export let textCache: KonvaText;
const layerCache = new Konva.Layer();

export const cleanCircleCache = () => {
    circleCache.destroy();
    textCache.destroy();
}

export const addCacheElement = (stage: Stage) => {

    circleCache = new Konva.Circle({
        x: 10,
        y: 10,
        radius: SIZE_CIRCLE / 2,
        fill: '#fff',
        perfectDrawEnabled: false,
    })

    textCache = new Konva.Text({
        x: 10,
        y: 10,
        text: `1`,
        fontSize: 10,
        fill: '#fff',
    })

    layerCache.add(circleCache);
    layerCache.add(textCache);

    stage.add(layerCache);
    circleCache.cache();
    textCache.cache();

    cleanCircleCache();
}

export const cloningElement = (props: PlaceElementType) => {

    const cloneCircle = circleCache.clone({
        x: props.positionPlace.x,
        y: props.positionPlace.y,
        radius: SIZE_CIRCLE / 2,
        fill: COLORS.bgPlace,
        onDragStart: props.onDragStart,
        onDragEnd: props.onDragEnd,
    })

    const cloneText = textCache.clone({
        x: props.positionPlace.x,
        y: props.positionPlace.y,
        text: `${props.numCol}`,
        fontSize: 10,
        fill: '#fff',
        offset: props.offset,
    })
    cloneCircle.cache();
    cloneText.cache();

    return {cloneCircle, cloneText};
}

import {PlaceType} from "../reducers/sectorsReducer";
import uuid from "react-uuid";
import {CountCirclesDrawType} from "../../components/main/content/layers/commonLayer/selectionArea/SelectionAreaGrid";
import {SIZE_CIRCLE, SIZE_IDENT_CIRCLE} from "../../components/figures/sectors/cacheCircle";

type CoordinateCalcType = {
    xStart: number,
    yStart: number,
    xEnd: number,
    yEnd: number
}
const sizeCircleWithIdent = SIZE_CIRCLE + SIZE_IDENT_CIRCLE;

const createCircle = (x: number, y: number, numberRow: number, numberColumn: number): PlaceType => {
    return {
        id: uuid(),
        x, y,
        numCol: numberRow,
        numRow: numberColumn,
        isSelected: false,
    }
}

export const calcCountCircleGrid = (coordinate: CoordinateCalcType) => {

    const width = coordinate.xEnd - coordinate.xStart;
    const height = coordinate.yEnd - coordinate.yStart;

    let countX;
    let countY;

    if (width === sizeCircleWithIdent) countX = 1;
    if (height === sizeCircleWithIdent) countY = 1;

    countX = width % sizeCircleWithIdent === 0 ? width / sizeCircleWithIdent : (width - sizeCircleWithIdent) / sizeCircleWithIdent;
    countY = height % sizeCircleWithIdent === 0 ? height / sizeCircleWithIdent : (height - sizeCircleWithIdent) / sizeCircleWithIdent;

    countX = Math.round(countX);
    countY = Math.round(countY);

    countX = Math.sign(countX) === 1 ? countX : -countX;
    countY = Math.sign(countY) === 1 ? countY : -countY;

    return {countX, countY};
}

export const createCirclesForGrid = async (coordinate: {xStart: number, yStart: number}, countCircleGrid: CountCirclesDrawType): Promise<PlaceType[]> => {

    return new Promise((resolve) => {

        let resultCircles = [];

        for (let x = 0; x < countCircleGrid.countX; x++) {

            for (let y = 0; y < countCircleGrid.countY; y++) {

                const pointX = coordinate.xStart + (x * sizeCircleWithIdent) + (SIZE_CIRCLE / 2);
                const pointY = coordinate.yStart + (y * sizeCircleWithIdent) + (SIZE_CIRCLE / 2);
                resultCircles.push(createCircle(pointX, pointY, x + 1, y + 1));
            }
        }
        resolve(resultCircles);
    })
}
